<script>
import axios from "axios";
export default {
  data() {
    return {
      apiFlagIndia: null,
      apiFlagModel: null,
      filter: "",
    };
  },
  props: {
    txtButtonGrafic: Text,
  },
  computed: {
    filterCountry() {
      if (!this.filter) return this.apiFlagModel;
      return this.apiFlagModel.filter((apiFlagModel) =>
        apiFlagModel.country.toLowerCase().includes(this.filter.toLowerCase())
      );
    },
  },
  mounted() {
    this.getApiWorld();
  },
  async mounted() {
    try {
      const results = await axios.get(`https://corona.lmao.ninja/v2/countries`);
      this.apiFlagModel = results.data.filter((dataSearchBrazil) => {
        return dataSearchBrazil.country === "Brazil";
      });
      this.apiFlagIndia = results.data.filter((flag) => {
        return flag.country === "India";
      });
      console.log(results.data);
    } catch (error) {
      console.log(error);
    }
  },
};
</script>

